import React from 'react';
import {Card, CardActions, CardContent, CardMedia, Button, Typography} from '@material-ui/core';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import DeleteIcon from '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz'
import moment from 'moment'; //for date n time
import useStyles from './styles';

const Post = ({post})=> {
    const classes = useStyles();

    return (
        <Card className={classes.card}>
                <CardMedia className={classes.media} image={Post.selectedFile} title={post.title}/>
                <div className={classes.overlay}>
                    <Typography variant="h6">{post.creator}</Typography>
                    <Typography variant="body2">{moment(post.creatorAt).fromNow()}</Typography>

                </div>
                <div className={classes.overlay2}>
                        <button style={{color : 'white'}} size="small" onClick={()=>{}}>
                            <MoreHorizIcon fontSize="default"/>
                        </button>
                </div>
                <div className={classes.details}>
                    <Typography variant="body2" colors="textSecondary">{post.tags.map((tag)=>`#${tag} `)}</Typography>
                </div>
                <CardContent><Typography className={classes.title} variant="h5" gutterBottom>{post.message}</Typography></CardContent>

                <CardActions className={classes.cardActions}>
                    <Button size="small" color="primary" onClick={() =>{}}><ThumbUpAltIcon fontSize="small" /> Like {post.likeCount} </Button>
                    <Button size="small" color="primary" onClick={() =>{}}><DeleteIcon fontSize="small" /> Delete</Button>
                </CardActions>
        </Card>
    )
}

export default Post;
